
require("sake/clean");

/**
 * We use sake's 'run' method to get CLOBBER to export into the global sake
 * run context.  In addition we assign it to the 'sake' global variable to
 * make it accessible those who only 'require' sake and use the object
 * directly.
 */
require("sake").run(function () {
    CLOBBER = new FileList().clearExcludes();
    sake.CLOBBER = CLOBBER;
    
    desc("Remove all files.");
    taskSync("clobber", ["clean"], function (t) {
        CLOBBER.existing.forEach(function (path) {
            rm_rf(path);
        });
    });
});