/**
 * We use sake's 'run' method to get CLEAN to export into the global sake
 * run context.  In addition we assign it to the 'sake' global variable to
 * make it accessible those who only 'require' sake and use the object
 * directly.
 */
require("sake").run(function () {
    CLEAN = new FileList();
    sake.CLEAN = CLEAN;
    
    desc("Remove temporary files.");
    taskSync("clean", function (t) {
        CLEAN.existing.forEach(function (path) {
            rm(path);
        }); 
    });
});
